import React, { useState, useEffect } from 'react';
import { Container, Table, Button, Spinner, Alert, Badge, Modal, Form, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import { API_URL } from '../../utils/constants';

const FAQsList = () => {
  const [faqs, setFaqs] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [currentFAQ, setCurrentFAQ] = useState({
    question: '',
    answer: '',
    category: 'General',
    order: 0,
    isActive: true
  });
  const [isEditing, setIsEditing] = useState(false);
  const [newCategory, setNewCategory] = useState('');
  const [showCategoryInput, setShowCategoryInput] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('all');

  useEffect(() => {
    fetchFAQs();
    fetchCategories();
  }, []);

  const fetchFAQs = async () => {
    try {
      setLoading(true);
      const { data } = await axios.get(`${API_URL}/api/faqs`);
      setFaqs(data);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to fetch FAQs');
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const { data } = await axios.get(`${API_URL}/api/faqs/categories`);
      setCategories(data);
    } catch (err) {
      console.error('Failed to fetch categories:', err);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setCurrentFAQ({
      ...currentFAQ,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleAddCategory = () => {
    if (newCategory.trim() && !categories.includes(newCategory.trim())) {
      setCategories([...categories, newCategory.trim()]);
      setCurrentFAQ({ ...currentFAQ, category: newCategory.trim() });
      setNewCategory('');
      setShowCategoryInput(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      if (isEditing) {
        await axios.put(`${API_URL}/api/faqs/${currentFAQ._id}`, currentFAQ);
      } else {
        await axios.post(`${API_URL}/api/faqs`, currentFAQ);
      }
      
      fetchFAQs();
      fetchCategories();
      handleCloseModal();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save FAQ');
    }
  };

  const handleEdit = (faq) => {
    setCurrentFAQ(faq);
    setIsEditing(true);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this FAQ?')) {
      try {
        await axios.delete(`${API_URL}/api/faqs/${id}`);
        setFaqs(faqs.filter(faq => faq._id !== id));
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to delete FAQ');
      }
    }
  };

  const handleToggleActive = async (id, currentStatus) => {
    try {
      await axios.put(`${API_URL}/api/faqs/${id}`, { isActive: !currentStatus });
      setFaqs(faqs.map(faq => 
        faq._id === id ? { ...faq, isActive: !currentStatus } : faq
      ));
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update FAQ status');
    }
  };

  const handleAddNew = () => {
    setCurrentFAQ({
      question: '',
      answer: '',
      category: 'General',
      order: 0,
      isActive: true
    });
    setIsEditing(false);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setNewCategory('');
    setShowCategoryInput(false);
  };

  const filteredFaqs = selectedCategory === 'all' 
    ? faqs 
    : faqs.filter(faq => faq.category === selectedCategory);

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '70vh' }}>
        <Spinner animation="border" variant="primary" />
      </div>
    );
  }

  return (
    <Container fluid>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>FAQs</h2>
        <Button variant="primary" onClick={handleAddNew}>Add New FAQ</Button>
      </div>

      {error && <Alert variant="danger">{error}</Alert>}

      <div className="mb-4">
        <Row>
          <Col md={6}>
            <Form.Group>
              <Form.Label>Filter by Category</Form.Label>
              <Form.Select 
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                <option value="all">All Categories</option>
                {categories.map((category, index) => (
                  <option key={index} value={category}>{category}</option>
                ))}
              </Form.Select>
            </Form.Group>
          </Col>
        </Row>
      </div>

      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Question</th>
            <th>Answer</th>
            <th>Category</th>
            <th>Order</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredFaqs.length === 0 ? (
            <tr>
              <td colSpan="6" className="text-center">No FAQs found</td>
            </tr>
          ) : (
            filteredFaqs.map(faq => (
              <tr key={faq._id}>
                <td>{faq.question}</td>
                <td>{faq.answer.substring(0, 100)}{faq.answer.length > 100 ? '...' : ''}</td>
                <td>{faq.category}</td>
                <td>{faq.order}</td>
                <td>
                  <Badge bg={faq.isActive ? 'success' : 'secondary'}>
                    {faq.isActive ? 'Active' : 'Inactive'}
                  </Badge>
                </td>
                <td>
                  <Button 
                    variant="info" 
                    size="sm" 
                    className="me-2"
                    onClick={() => handleEdit(faq)}
                  >
                    Edit
                  </Button>
                  <Button 
                    variant={faq.isActive ? 'warning' : 'success'} 
                    size="sm" 
                    className="me-2"
                    onClick={() => handleToggleActive(faq._id, faq.isActive)}
                  >
                    {faq.isActive ? 'Deactivate' : 'Activate'}
                  </Button>
                  <Button 
                    variant="danger" 
                    size="sm"
                    onClick={() => handleDelete(faq._id)}
                  >
                    Delete
                  </Button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </Table>

      {/* FAQ Add/Edit Modal */}
      <Modal show={showModal} onHide={handleCloseModal} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit FAQ' : 'Add New FAQ'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Question</Form.Label>
              <Form.Control 
                type="text"
                name="question"
                value={currentFAQ.question}
                onChange={handleInputChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Answer</Form.Label>
              <Form.Control 
                as="textarea"
                rows={4}
                name="answer"
                value={currentFAQ.answer}
                onChange={handleInputChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Category</Form.Label>
              {showCategoryInput ? (
                <div className="d-flex mb-2">
                  <Form.Control 
                    type="text"
                    value={newCategory}
                    onChange={(e) => setNewCategory(e.target.value)}
                    placeholder="Enter new category"
                  />
                  <Button 
                    variant="success" 
                    className="ms-2"
                    onClick={handleAddCategory}
                  >
                    Add
                  </Button>
                  <Button 
                    variant="outline-secondary" 
                    className="ms-2"
                    onClick={() => setShowCategoryInput(false)}
                  >
                    Cancel
                  </Button>
                </div>
              ) : (
                <div className="d-flex">
                  <Form.Select
                    name="category"
                    value={currentFAQ.category}
                    onChange={handleInputChange}
                    className="me-2"
                  >
                    {categories.map((category, index) => (
                      <option key={index} value={category}>{category}</option>
                    ))}
                  </Form.Select>
                  <Button 
                    variant="outline-primary"
                    onClick={() => setShowCategoryInput(true)}
                  >
                    New Category
                  </Button>
                </div>
              )}
            </Form.Group>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Display Order</Form.Label>
                  <Form.Control 
                    type="number"
                    name="order"
                    value={currentFAQ.order}
                    onChange={handleInputChange}
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Check 
                    type="checkbox"
                    label="Active"
                    name="isActive"
                    checked={currentFAQ.isActive}
                    onChange={handleInputChange}
                  />
                </Form.Group>
              </Col>
            </Row>

            <div className="d-flex justify-content-end">
              <Button variant="secondary" className="me-2" onClick={handleCloseModal}>
                Cancel
              </Button>
              <Button variant="primary" type="submit">
                Save
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default FAQsList;
