import React, { useState, useEffect } from 'react';
import { Container, Form, Button, Card, Alert, Spinner, Row, Col, Nav, Tab } from 'react-bootstrap';
import axios from 'axios';
import { API_URL } from '../../utils/constants';

const SiteSettings = () => {
  const [settings, setSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [activeTab, setActiveTab] = useState('general');
  const [logoPreview, setLogoPreview] = useState(null);
  const [heroImagePreview, setHeroImagePreview] = useState(null);
  const [aboutImagePreview, setAboutImagePreview] = useState(null);
  
  // File uploads
  const [logo, setLogo] = useState(null);
  const [heroImage, setHeroImage] = useState(null);
  const [aboutImage, setAboutImage] = useState(null);

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      setLoading(true);
      const { data } = await axios.get(`${API_URL}/api/settings`);
      
      // Ensure all required nested objects are initialized
      const preparedSettings = {
        ...data,
        contactInfo: data.contactInfo || {
          address: '',
          phone: '',
          email: '',
          hours: ''
        },
        socialMedia: data.socialMedia || {
          facebook: '',
          twitter: '',
          instagram: '',
          linkedin: ''
        },
        hero: data.hero || {
          title: '',
          subtitle: '',
          backgroundUrl: '',
          buttonText: '',
          buttonLink: ''
        },
        about: data.about || {
          title: '',
          content: '',
          imageUrl: ''
        }
      };
      
      setSettings(preparedSettings);
      if (preparedSettings.logoUrl) {
        setLogoPreview(`${API_URL}/uploads/${preparedSettings.logoUrl}`);
      }
      if (preparedSettings.hero && preparedSettings.hero.backgroundUrl) {
        setHeroImagePreview(`${API_URL}/uploads/${preparedSettings.hero.backgroundUrl}`);
      }
      if (preparedSettings.about && preparedSettings.about.imageUrl) {
        setAboutImagePreview(`${API_URL}/uploads/${preparedSettings.about.imageUrl}`);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to load settings');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (section, field, value) => {
    if (section) {
      setSettings({
        ...settings,
        [section]: {
          ...settings[section],
          [field]: value
        }
      });
    } else {
      setSettings({
        ...settings,
        [field]: value
      });
    }
  };

  const handleFileChange = (e, type) => {
    const file = e.target.files[0];
    if (!file) return;

    // Preview the selected image
    const reader = new FileReader();
    reader.onload = () => {
      if (type === 'logo') {
        setLogoPreview(reader.result);
        setLogo(file);
      } else if (type === 'hero') {
        setHeroImagePreview(reader.result);
        setHeroImage(file);
      } else if (type === 'about') {
        setAboutImagePreview(reader.result);
        setAboutImage(file);
      }
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(false);
    setSaving(true);
    
    try {
      const formData = new FormData();
      
      // Add basic settings
      formData.append('siteName', settings.siteName);
      
      // Add contact info
      formData.append('contactInfo[address]', settings.contactInfo.address);
      formData.append('contactInfo[phone]', settings.contactInfo.phone);
      formData.append('contactInfo[email]', settings.contactInfo.email);
      formData.append('contactInfo[hours]', settings.contactInfo.hours);
      
      // Add social media
      Object.keys(settings.socialMedia).forEach(platform => {
        formData.append(`socialMedia[${platform}]`, settings.socialMedia[platform] || '');
      });
      
      // Add hero section
      formData.append('hero[title]', settings.hero.title);
      formData.append('hero[subtitle]', settings.hero.subtitle);
      formData.append('hero[buttonText]', settings.hero.buttonText);
      formData.append('hero[buttonLink]', settings.hero.buttonLink);
      
      // Add about section
      formData.append('about[title]', settings.about.title);
      formData.append('about[content]', settings.about.content);
      
      // Add files if selected
      if (logo) {
        formData.append('logo', logo);
      }
      if (heroImage) {
        formData.append('heroBackground', heroImage);
      }
      if (aboutImage) {
        formData.append('aboutImage', aboutImage);
      }
      
      await axios.put(`${API_URL}/api/settings`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      setSuccess(true);
      setTimeout(() => setSuccess(false), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save settings');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '70vh' }}>
        <Spinner animation="border" variant="primary" />
      </div>
    );
  }

  if (!settings) {
    return (
      <Alert variant="danger">
        Failed to load settings. Please try refreshing the page.
      </Alert>
    );
  }

  return (
    <Container fluid>
      <h2 className="mb-4">Site Settings</h2>
      
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">Settings saved successfully!</Alert>}
      
      <Tab.Container activeKey={activeTab} onSelect={setActiveTab}>
        <Row>
          <Col md={3} lg={2}>
            <Nav variant="pills" className="flex-column mb-4">
              <Nav.Item>
                <Nav.Link eventKey="general">General</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="contact">Contact Info</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="social">Social Media</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="hero">Hero Section</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="about">About Section</Nav.Link>
              </Nav.Item>
            </Nav>
          </Col>
          
          <Col md={9} lg={10}>
            <Card>
              <Card.Body>
                <Form onSubmit={handleSubmit}>
                  <Tab.Content>
                    {/* General Settings */}
                    <Tab.Pane eventKey="general">
                      <h4 className="mb-4">General Settings</h4>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Site Name</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.siteName}
                          onChange={(e) => handleInputChange(null, 'siteName', e.target.value)}
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-4">
                        <Form.Label>Logo</Form.Label>
                        {logoPreview && (
                          <div className="mb-2">
                            <img 
                              src={logoPreview} 
                              alt="Logo preview" 
                              style={{ height: '80px', marginBottom: '10px', display: 'block' }} 
                            />
                          </div>
                        )}
                        <Form.Control 
                          type="file" 
                          accept="image/*"
                          onChange={(e) => handleFileChange(e, 'logo')}
                        />
                      </Form.Group>
                    </Tab.Pane>
                    
                    {/* Contact Info */}
                    <Tab.Pane eventKey="contact">
                      <h4 className="mb-4">Contact Information</h4>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Address</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.contactInfo.address}
                          onChange={(e) => handleInputChange('contactInfo', 'address', e.target.value)}
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Phone</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.contactInfo.phone}
                          onChange={(e) => handleInputChange('contactInfo', 'phone', e.target.value)}
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Email</Form.Label>
                        <Form.Control 
                          type="email"
                          value={settings.contactInfo.email}
                          onChange={(e) => handleInputChange('contactInfo', 'email', e.target.value)}
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Hours</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.contactInfo.hours}
                          onChange={(e) => handleInputChange('contactInfo', 'hours', e.target.value)}
                        />
                      </Form.Group>
                    </Tab.Pane>
                    
                    {/* Social Media */}
                    <Tab.Pane eventKey="social">
                      <h4 className="mb-4">Social Media Links</h4>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Facebook</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.socialMedia.facebook || ''}
                          onChange={(e) => handleInputChange('socialMedia', 'facebook', e.target.value)}
                          placeholder="https://facebook.com/yourpage"
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Twitter</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.socialMedia.twitter || ''}
                          onChange={(e) => handleInputChange('socialMedia', 'twitter', e.target.value)}
                          placeholder="https://twitter.com/yourhandle"
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Instagram</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.socialMedia.instagram || ''}
                          onChange={(e) => handleInputChange('socialMedia', 'instagram', e.target.value)}
                          placeholder="https://instagram.com/yourhandle"
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>LinkedIn</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.socialMedia.linkedin || ''}
                          onChange={(e) => handleInputChange('socialMedia', 'linkedin', e.target.value)}
                          placeholder="https://linkedin.com/company/yourcompany"
                        />
                      </Form.Group>
                    </Tab.Pane>
                    
                    {/* Hero Section */}
                    <Tab.Pane eventKey="hero">
                      <h4 className="mb-4">Hero/Banner Section</h4>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Title</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.hero.title}
                          onChange={(e) => handleInputChange('hero', 'title', e.target.value)}
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Subtitle</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.hero.subtitle}
                          onChange={(e) => handleInputChange('hero', 'subtitle', e.target.value)}
                        />
                      </Form.Group>
                      
                      <Row>
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Button Text</Form.Label>
                            <Form.Control 
                              type="text"
                              value={settings.hero.buttonText}
                              onChange={(e) => handleInputChange('hero', 'buttonText', e.target.value)}
                            />
                          </Form.Group>
                        </Col>
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Button Link</Form.Label>
                            <Form.Control 
                              type="text"
                              value={settings.hero.buttonLink}
                              onChange={(e) => handleInputChange('hero', 'buttonLink', e.target.value)}
                            />
                          </Form.Group>
                        </Col>
                      </Row>
                      
                      <Form.Group className="mb-4">
                        <Form.Label>Background Image</Form.Label>
                        {heroImagePreview && (
                          <div className="mb-2">
                            <img 
                              src={heroImagePreview} 
                              alt="Hero background preview" 
                              style={{ maxWidth: '100%', height: 'auto', maxHeight: '200px', marginBottom: '10px', display: 'block' }} 
                            />
                          </div>
                        )}
                        <Form.Control 
                          type="file" 
                          accept="image/*"
                          onChange={(e) => handleFileChange(e, 'hero')}
                        />
                      </Form.Group>
                    </Tab.Pane>
                    
                    {/* About Section */}
                    <Tab.Pane eventKey="about">
                      <h4 className="mb-4">About Section</h4>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Title</Form.Label>
                        <Form.Control 
                          type="text"
                          value={settings.about.title}
                          onChange={(e) => handleInputChange('about', 'title', e.target.value)}
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-3">
                        <Form.Label>Content</Form.Label>
                        <Form.Control 
                          as="textarea"
                          rows={5}
                          value={settings.about.content}
                          onChange={(e) => handleInputChange('about', 'content', e.target.value)}
                        />
                      </Form.Group>
                      
                      <Form.Group className="mb-4">
                        <Form.Label>Image</Form.Label>
                        {aboutImagePreview && (
                          <div className="mb-2">
                            <img 
                              src={aboutImagePreview} 
                              alt="About section image preview" 
                              style={{ maxWidth: '100%', height: 'auto', maxHeight: '200px', marginBottom: '10px', display: 'block' }} 
                            />
                          </div>
                        )}
                        <Form.Control 
                          type="file" 
                          accept="image/*"
                          onChange={(e) => handleFileChange(e, 'about')}
                        />
                      </Form.Group>
                    </Tab.Pane>
                  </Tab.Content>
                  
                  <div className="d-flex justify-content-end mt-4">
                    <Button 
                      type="submit" 
                      variant="primary" 
                      disabled={saving}
                    >
                      {saving ? 'Saving...' : 'Save Settings'}
                    </Button>
                  </div>
                </Form>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Tab.Container>
    </Container>
  );
};

export default SiteSettings;
